import React from "react";
import './item.css';
import { Link } from "react-router-dom";

const Item=(props)=>{
    return(
        <div className="item">
       <Link to ={`/product/${props.id}`}>   <img src={props.image} alt=""></img></Link>
            <p>{props.name}</p>
            <div className="item-prices">
                <div className="item-price-new">
                INR {props.new_price}
                </div>
                <div className="item-price-old">
                INR {props.old_price}
                </div>
            </div>

        </div>
    )
}
export default Item;
